uniform float uSize;varying vec2 texCoord;void main(){	const float sz = 0.3;	const float sw = 1.0;	const float hs = 100000000.0;		float x = texCoord.x * uSize;	float y = texCoord.y * uSize;	float p = ( x * x ) + ( y * y ) - sz;	float len = ( p * p * p ) + ( sw * x * x * y * y * y );	len = clamp( len * hs, 0.0, 1.0 );	float alp = ceil( 1.0 - len );	gl_FragColor = vec4( 1.0, 0.0, 0.0, alp );}